{
                if (testRunner.testCase.testSteps[r.testStep.name].config.type=="restrequest")
                {
                                f.append( "\nTestStep " + r.testStep.name + " finished with status: " + r.status +"\r\n")
          f.append("==================Request/Response Below for "+r.testStep.name+"=============================\n")
          f.append("Raw request: \n"+new String(testRunner.testCase.testSteps[r.testStep.name].testRequest.response.rawRequestData)+"\n\n")
          f.append("Raw response: \n"+new String(testRunner.testCase.testSteps[r.testStep.name].testRequest.response.rawResponseData))
          f.append("\n==================Request/Response End for "+r.testStep.name+"=============================\n")
          f.append("\nr.status: "+r.status)
          status=String.valueOf(r.status)
          if (status=="FAILED")
          {
               f1.append("\nTest Step Failed: "+r.testStep.name)
               f1.append("==================Request/Response Below for FAILED Test Step: "+r.testStep.name+"=============================\n")
               f1.append("Raw request: \n"+new String(testRunner.testCase.testSteps[r.testStep.name].testRequest.response.rawRequestData)+"\n\n")
               f1.append("Raw response: \n"+new String(testRunner.testCase.testSteps[r.testStep.name].testRequest.response.rawResponseData))
               f1.append("\n==================Request/Response End for FAILED Test Step: "+r.testStep.name+"=============================\n\n")
               }
                }
}

